import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

//Bootstrap stuff

var Button = require('react-bootstrap/lib/Button');
var Form = require('react-bootstrap/lib/Form');
var FormGroup = require('react-bootstrap/lib/FormGroup');
var FormControl = require('react-bootstrap/lib/FormControl');
var ControlLabel = require('react-bootstrap/lib/ControlLabel');

class App extends Component {
    constructor(props) {
        super(props)

        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.func = this.func.bind(this);
        this.initiate_or_stop_sequence = this.initiate_or_stop_sequence.bind(this);

        this.state = {
            frequencyValue : 0,
            counter : 0,
            beerValue : 0,
            opacityValue : 0.3,
            interValId : "",
            run : true,
            buttonText : "Initiera Dryckessekvens",
            buttonClass : "success"
        }
    }

    handleChange(e) {
        this.setState({frequencyValue : e.target.value})
    }

    handleClick() {
        this.setState({opacityValue : 1})
    }

    func() {
        console.log("HAHA")
    }

    initiate_or_stop_sequence () {
        if (this.state.run) {
            console.log("RUNNING")
            this.setState({
                buttonText : "Avsluta Dryckessekvens",
                buttonClass : "danger",
                interValId : setInterval(this.func, 1000),
                run : false
            })
        } else {
        console.log("STOP")
            this.setState({
                buttonText : "Initiera Dryckessekvens",
                buttonClass : "success",
                run : true
            })
            clearInterval(this.state.interValId)
        }
    }

  render() {
    var imgDivStyle = {
        opacity : this.state.opacityValue,
    }

    var infoDivStyle = {
        color: "white"
    }

    var buttonStyle = {
        height : "10vh"
    }

    var formDivStyle={
        width : "10%",
        margin: "auto"
    }

    return (
      <div className="App">
        <div style={infoDivStyle}>
            <h2>Osäker på när det är dags för vätskepaus?</h2> 
            <h2>Använd vår lösning som med hjälp av machine learning, AI, blockchain, och management beräknar när det är dags att återfukta.</h2>
            <h2>BEERCALC - För att du inte har någon fantasi.</h2>
            <div style={formDivStyle}>
            <form>
                <FormGroup>

                <ControlLabel>Vilken frekvens önskas?</ControlLabel>
                <FormControl
                    type="text"
                    value={this.state.frequencyValue}
                    placeholder="0"
                    onChange={this.handleChange}
                />
                </FormGroup>
            </form>
            </div>
            <Button onClick={this.initiate_or_stop_sequence} style={buttonStyle} bsStyle={this.state.buttonClass} >Initiera dryckessekvens</Button>
        </div>
        <Row>
            <Col>
            </Col>
            <Col style={{opacity : this.state.opacityValue}}>
                <img id="prippan" style={{height: "60vh"}} src={require('./media/pripps.png')} />
            </Col>
            <Col>
            </Col>
        </Row>
      </div>
    );
  }
}

export default App;
